INSERT INTO `posts` (`id`, `category_id`, `title`, `text`, `image`, `active`, `slider`, `newsbar`, `view`, `user_id`, `date`) VALUES
(3, 5, 'تیپ عجیب و غریب مدافع لیگ برتری', '<p>به گزارش خبرنگار دنیای ورزش گروه ورزش باشگاه خبرنگاران جوان، هکتور بیرین مدافع راست تیم فوتبال آرسنال انگلیس از سری بازیکنانی است که علاقه زیادی به صنعت مد و لباس دارد و استایل&zwnj;های او همیشه مورد توجه نشریات مختلف ورزشی قرار است.</p>\r\n<p>نشریه بلیچر ریپورتز در گزارش خود استایل&zwnj;های عجیب و غریب او را جمع آوری کرد و در قالب یک گلچین عکس آن را سوژه رسانه&zwnj;ای کرد. پیشتر بیرین اعلام کرده بود از تیپ&zwnj;های عجیب و غریبش لذت می&zwnj;برد و پشت هر کدام داستانی خاص نهفته است و مردم با دیدنشان الهام می&zwnj;گیرند.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '../uploadFiles/img/img24.jpg ', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(4, 5, 'آموزش گام به گام ورزش‌های آپارتمانی', '<p>به گزارش گروه ورزش باشگاه خبرنگاران جوان، پس از اینکه ویروس کرونا اولین بار در ۱۹ دسامبر ۲۰۲۰ در چین رویت شد این بیماری به سرعت در اقصی نقاط جهان گسترش یافت که کشور ما ایران هم از این قضیه مثتثنی نبود.</p>\r\n<p>به دنبال شیوع ویروس کرونا اکثر مردم مجبور شدند در خانه بمانند و به بیرون مراجعه نکنند. بسیاری از مردم جهت انجام فعالیت ورزشی نمی توانند به فضای باز مانند پارک و باشگاه مراجعه کنند و مجبور هستند در خانه برخی از حرکات ورزشی را انجام دهند.</p>\r\n<p>گروه ورزشی باشگاه خبرنگاران جوان، به همین دلیل تصمیم به آموزش ۴ ورزشی (آمادگی جسمانی - تی آر ایکس - پیلاتس و ایروپامپ) که نیاز به فعالیت خارج از منزل ندارند و می شود آن&zwnj;ها را در خانه انجام داد، گرفت.</p>\r\n<p><img src=\"/phpbox/template/uploadFiles/img/11573731_863.jpg?1584776652950\" alt=\"11573731_863\" /></p>\r\n<p>رسیدن به سطح مطلوبی از آمادگی جسمانی، کم&zwnj;وبیش خواسته &nbsp;همگی ما است که می&zwnj;تواند برای سلامتی بدن بسیار مفید باشد. طبق تعریفی که وزارت بهداشت کشور آمریکا از آمادگی جسمانی ارائه کرده است، آمادگی جسمانی به &laquo;ویژگی&zwnj;ها و توانایی&zwnj;های مرتبط با مهارت در انجام فعالیت&zwnj;های جسمانی گفته می&zwnj;شود. بعضی افراد این توانایی&zwnj;ها و ویژگی&zwnj;ها را به&zwnj;طور طبیعی دارند و بعضی دیگر، به&zwnj; مرور زمان آن&zwnj;ها را به دست می&zwnj;آورند.&raquo;</p>\r\n<p>آمادگی جسمانی بر سلامت دستگاه قلبی و عروقی، قدرت و استقامت عضلانی، ریه و کارکرد آن موثر است</p>', '../uploadFiles/img/11573729_757.jpg', 1, 1, 1, 0, 1, '2020-03-21 00:00:00'),
(5, 5, 'پیام سرمربی سابق تیم ملی فوتبال درباره کرونا', '<p>به گزارش خبرنگار فوتبال و فوتسال گروه ورزشی باشگاه خبرنگاران جوان، کارلوس کی روش سرمربی سابق تیم ملی فوتبال کشورمان که اکنون در تیم ملی کلمبیا حضور دارد در تازه&zwnj;ترین پیام خود در صفحه شخصی اینستاگرام خود درباره شیوع ویروس کرونا که بیشتر نقاط جهان را در بر گرفته است، برای مردم جهان پیام جالبی را منتشر کرد. او در صفحه شخصی خود اینگونه نوشت: برای آن که بتوانیم ویروس کرونا را با قدرت شکست دهیم، همگی باید نکات بهداشتی را به خوبی رعایت کرده و تا آن جا که امکان دارد از خانه&zwnj;های خود خارج نشده و در آن جا بمانید.</p>\r\n<p><img src=\"/phpbox/template/uploadFiles/img/11586166_967.jpg?1584776918447\" alt=\"11586166_967\" /></p>\r\n<p>همچنین کی روش در بخش پایانی پیام خود در صفحه شخصی اینستاگرام ضمن فرا رسیدن سال جدید، نوروز ۹۹ را به مردم ایران تبریک گفت و برای تمامی مردم ایران آرزوی سلامتی کرد.</p>', '../uploadFiles/img/11586166_967.jpg', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(6, 5, 'تبریک اینستاگرامی AFC به مناسبت روز تولد علی دایی', '<p>به گزارش خبرنگار حوزه اخبار داغ گروه فضای مجازی باشگاه خبرنگاران جوان، کنفدراسیون فوتبال آسیا (AFC) به مناسبت روز تولد ۵۱ سالگی علی دایی، بهترین گلزن تیم ملی، پستی را در صفحه اینستاگرام خود منتشر کرد و این روز را تبریک گفت.</p>\r\n<p>علی دایی متولد اول فروردین سال ۴۸ در اردبیل است که یکی از بهترین مهاجمان تاریخ جام ملت&zwnj;های آسیا به شمار می&zwnj;رود. این بازیکن در دوران حرفه&zwnj;ای فوتبال خود با زدن ۱۰۹ گل در ۱۴۹ بازی ملی، رکورد بیشترین گل زده در بازی&zwnj;های ملی فوتبال مردان جهان را به&zwnj;دست&zwnj;آورد.</p>\r\n<p>در ادامه پست اینستاگرامی AFC را برای تولد علی دایی مشاهده می&zwnj;کنید.</p>\r\n<p><img src=\"/phpbox/template/uploadFiles/img/11586007_592.png?1584777130707\" alt=\"11586007_592\" /></p>', '../uploadFiles/img/11586007_592.png', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(7, 4, 'بازی‌های رایانه‌ای جدید نهاجا در حال ساخت هستند', '<p>جندقی مدیر مرکز تولید محتوای نیروی هوایی ارتش جمهوری اسلامی ایران در گفت&zwnj;وگو با خبرنگار حوزه دفاعی امنیتی گروه سیاسی باشگاه خبرنگاران جوان، &nbsp;درباره طراحی و تولید بازی موبایلی سرباز قهرمان در نیروی هوایی ارتش گفت: بازی اندروید سرباز قهرمان محصولی مشترک بین نیروی هوایی ارتش و سازمان وظیفه عمومی ناجاست که با تخصص پرسنل و سربازان وظیفه نیروی هوایی ارتش از شهریور ماه سال ۹۸ به مدت پنج ماه ساخته شده است.</p>\r\n<p>&nbsp;</p>\r\n<p>او ادامه داد: ماموریت&zwnj;های مختلفی که یک سرباز می تواند در حین خدمت با آن مواجه شود را در این بازی طراحی کرده ایم و آن را تحت عنوان مراحل مختلف بازی استفاده کردیم.</p>\r\n<p>مدیر مرکز تولید محتوای نهاجا افزود: طبق برنامه ریزی&zwnj;ها قرار است این بازی در به روز رسانی&zwnj;های بعدی ارتقا یابد که در این مسیر باز هم از سربازان وظیفه و نخبه استفاده خواهیم کرد.</p>\r\n<p>جندقی تصریح کرد: در حال حاضر با توجه به تعداد مخاطب در کشور پلتفرم تولیدی ما اندروید است، ولی در آینده برنامه&zwnj;ای برای ورود به پلتفرم آی او اس داریم.</p>\r\n<p>او درباره آینده بازی سازی در نهاجا گفت: بازی&zwnj;های جدید در مرحله تولید قرار دارند که بیشتر آن&zwnj;ها در ژانرهوایی هستند و قرار است ماموریت&zwnj;ها و عملیات&zwnj;های نیروی هوایی ارتش در سال&zwnj;های گذشته و به ویژه دوران دفاع مقدس در قالب مراحل مختلف به بازیکنان ارائه شود.</p>\r\n<p>مدیر مرکز تولید محتوای نهاجا در پایان اظهار کرد: با توجه به ژانر و کیفیت بازی&zwnj;ها در بازخور&zwnj;های مخاطبان رضایت خوبی مشاهده شد. در محصولات آمار دانلود و نصب متفاوت است، اما در حوزه بازی&zwnj;ها تا سیصد هزار دریافت هم داشته ایم که با توجه به تجارب قبلی امیدواریم این آمار افزایش یابد.</p>\r\n<p>گفتنی است با توجه به اهمیت فعالیت در حوزه&zwnj;های مختلف فضای مجازی، نیروی هوایی ارتش در حوزه جنگ نرم و مقابله با عملیات روانی دشمن برنامه&zwnj;های متعددی در دست اقدام دارد که عمده این برنامه&zwnj;ها برای کارکنان پایور و نیرو&zwnj;های وظیفه که در معترض تهدیدات بیشتر دشمنان هستند، انجام می&zwnj;شود.</p>', '../uploadFiles/img/img16.jpg', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(8, 4, 'ثبت دادخواستی در پارلمان انگلیس ', '<p>به گزارش حوزه سیاست خارجی گروه سیاسی باشگاه خبرنگاران جوان،&laquo;حمید بعیدی نژاد&raquo; سفیر جمهوری اسلامی ایران در لندن امشب (جمعه) در صفحه توئیترش نوشت: &laquo;پارلمان انگلیس با ثبت &laquo;دادخواستی&raquo;، از دولت آمریکا تقاضا کرده است که فوراً تحریم&zwnj;های خود علیه ایران را در دوره بیماری کرونا لغو کند.&raquo;</p>\r\n<p>وی افزود: &laquo;این دادخواست را تا این لحظه بیش از ۱۱۰۰ نفر امضا کرده اند. اتباع و اقامت کنندگان بریتانیا می توانند این درخواست را امضا کنند.&raquo;</p>\r\n<p>&nbsp;</p>', '../uploadFiles/img/11585317_739.jpg', 1, 1, 1, 0, 1, '2020-03-21 00:00:00'),
(9, 4, 'مخالفت جدی روسیه با تحریم‌های آمریکا در مقابله ایران ', '<p>به گزارش حوزه سیاست خارجی گروه سیاسی باشگاه خبرنگاران جوان، &laquo;کاظم جلالی&raquo; سفیر جمهوری اسلامی ایران در فدراسیون روسیه امروز (جمعه) اول فروردین ماه ۱۳۹۹ در گفت&zwnj;وگوی تلفنی با &laquo;یوری اوشاکوف&raquo; دستیار رئیس جمهور روسیه، تروریسم بهداشتی علیه ایران، آخرین وضعیت گسترش ویروس کرونا در سطح جهان و اقدامات صورت گرفته توسط جمهوری اسلامی برای مبارزه با این پدیده شوم را مورد بحث و بررسی قرار داد.</p>\r\n<p>سفیر ایران در این گفتگو به تروریسم بهداشتی علیه ایران و تحریم&zwnj;های گسترده ایالات متحده آمریکا علیه ملت ایران به خصوص مانع تراشی&zwnj;های متعدد در ارسال کمک&zwnj;های دارویی و پزشکی به ایران جهت مبارزه با کرونا پرداخت.&zwnj;</p>\r\n<p>جلالی همچنین از کمک&zwnj;های بشردوستانه روسیه به ایران و ارسال کیت&zwnj;های تشخیص و همچنین حمایت&zwnj;های سیاسی روسیه از ایران قدردانی کرد.</p>\r\n<p>در این گفتگو اوشاکوف دستیار پوتین رئیس جمهور روسیه بر مخالفت جدی روسیه با تحریم&zwnj;های آمریکا علیه ایران، به خصوص در شرایط مقابله ایران با کرونا تاکید کرد.</p>\r\n<p>طرفین در خصوص راه اندازی کمپین با محوریت روسیه و چین برای لغو تحریم&zwnj;ها علیه ایران رایزنی کردند.</p>\r\n<p>جمعی از مقامات پارلمانی روسیه نیز پیش از این در تماس تلفنی سفیر ایران در مسکو بر ضرورت رفع تحریم&zwnj;های غیرقانونی و یکجانبه دولت آمریکا و تداوم کمک&zwnj;های انساندوستانه روسیه برای مقابله با کرونا تاکید کردند.(اینجا بخوانید)</p>', '../uploadFiles/img/11585229_242.jpg', 1, 0, 1, 0, 1, '2020-03-21 00:00:00'),
(10, 4, 'اصلاح قانون انتخابات اولویت مهم مجلس یازدهم است', '<p>الله&zwnj;وردی دهقانی منتخب مردم ورزقان در مجلس یازهم در گفت&zwnj;وگو با خبرنگار حوزه احزاب و تشکل&zwnj;های گروه سیاسی باشگاه خبرنگاران جوان، درباره اولویت&zwnj;های مجلس یازدهم گفت: اصلاح ساختار دولت و قوه اجرایی کشور باید اصلی ترین هدف مجلس یازدهم باشد.</p>\r\n<p>او افزود: مجلس امروز دچار روزمرگی شده و برنامه خاصی برای بهبود معیشت مردم و پیشبرد چرخه اقتصاد و صنعت ندارد و این در شرایطی است که تحریم ها هم مشکل ساز شده اند.</p>\r\n<p>دهقانی درباره شاخص&zwnj;های پیشرفت در کشور گفت: باید تمامی توانایی های کشور در همه حوزه ها همچون دانشگاه و مراکز تحقیقاتی، دستگاه های اجرایی و حتی سیستم ها و دستگاه های نظامی را به میدان بیاوریم تا ساختار صنعت و مدیریت کشور را متحول کنیم.</p>\r\n<p>منتخب مردم ورزقان در مجلس یازهم ادامه داد: وزارتخانه ها، نهادها و سازمان ها و در مجموع سیستم اجرایی کشور در شرایط فعلی باید برای بهبود وضعیت اقتصاد، صنعت و معیشت، الگوی موفق صنعت نظامی را مد نظر قرار دهند و طبق آن حرکت کنند.</p>\r\n<p>او با اشاره به لزوم اجرای مدیریت جهادی در کشور تصریح کرد: باید به شکل جهادی و با فکر و ایده های نو و خلاقانه وارد میدان شد و قوانین جدید وضع و قوانین گذشته را پالایش کرد.</p>\r\n<p>دهقانی در پایان تاکید کرد: میدان دادن به مردم، کوچک سازی و کاهش هزینه های دولت و از طرفی دیگر افزایش درآمد دولت با مالیات و میدان دادن به بخش خصوصی می توانند اولویت های مجلس یازدهم باشد تا اقتصاد خود را بهبود ببخشیم و از امکانات کشور به بهترین شکل استفاده کنیم.</p>', '../uploadFiles/img/11554916_453.jpg', 1, 1, 1, 0, 1, '2020-03-21 00:00:00'),
(11, 3, 'آنچه باید درباره دریافت چک و سفته ضمانت از کارگر توسط کارفرما بدانید', '<p>به گزارش خبرنگار حوزه رفاه و تعاون گروه اجتماعی باشگاه خبرنگاران جوان، در قانون کار به اخذ ضمانت از کارگران به هنگام عقد قرارداد با کارفرما هیچ اشاره&zwnj;ای نشده است.</p>\r\n<p>همچنین در قانون کار هیچ اشاره&zwnj;ای به این نشده است که کارفرما بابت حسن انجام کار از کارگر سفته یا چکی را به عنوان ضمانت دریافت کند.</p>\r\n<p>از دیدگاه نمایندگان کارگران این موضوع یک بحث غیرقانونی است و عمدتاً در محاکم قضایی وقتی کارگر ادعا می&zwnj;کند که چک یا سفته&zwnj;ای تحت عنوان ضمانت توسط کارفرما از او اخذ شده است، ابطال آن اعلام می&zwnj;شود و کارفرما هم با این مدارک نمی&zwnj;تواند کاری انجام دهد.</p>\r\n<p>نکته قابل توجه این است که کارگرانی که به هر دلیل چک و سفته ضمانت گذاشته&zwnj;اند در وهله اول باید آن را با قید ضمانت کار بگذارند و در وهله دوم از شکایت نترسند و از طریق شورای حل اختلاف پیگیر حقوق خود باشند.</p>', '../uploadFiles/img/11586579_663.jpg ', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(12, 3, 'راهکارهایی برای ترک اعتیاد فرزندان معتاد در خانواده', '<p>محبوبه زرین قلم روانشناس و درمانگر اعتیاد در گفت&zwnj;وگو با خبرنگار حوزه رفاه و تعاون گروه اجتماعی باشگاه خبرنگاران جوان، درباره راهکارهای کمک به ترک اعتیاد فرزند معتاد در خانواده، اظهار کرد: خانواده شاید در شروع اعتیاد فرزند خود نقش خاصی نداشته باشد، اما در ادامه اعتیاد و بهبودی مطمئنا نقش موثری خواهد داشت.</p>\r\n<p>این درمانگر اعتیاد با بیان اینکه توجه به دو نکته در ترک اعتیاد فرزندان حائز اهمیت است، گفت: اول اینکه اعتیاد یک بیماری مزمن، پیش رونده و عود کننده است و درمان آن فرآیندی طولانی و زمان بر است و نیاز به صبر، حوصله و همراهی دارد و در نکته دوم، هیچ نسخه ثابتی برای درمان همه بیماران وجود ندارد.</p>\r\n<p>&nbsp;</p>\r\n<p>وی با بیان اینکه درمان اعتیاد، درمان شبکه&zwnj;ای و فرا مدرن است، افزود: درمان شبکه&zwnj;ای یعنی خانواده قرار است به عنوان بازوی کمکی در درمان به فرد معتاد و درمانگر کمک کند تا او در روند بهبودی باقی بماند و درمان فرا مدرن یعنی فرآیندی که در آن با فرد همراهی می&zwnj;شود تا خودش را بسازد و هویت یابی کند و از طریق انطباق پذیری مسیر زندگی&zwnj;اش را طی کند.</p>\r\n<p>زرین قلم تصریح کرد: خانواده پس از کسب اطلاعات در زمینه اعتیاد و تغییرات لازم با شکستن چرخه انکار می&zwnj;تواند باعث ترغیب فرد به سمت پذیرش، درمان و بهبودی شود، پس ایجاد انگیزه برای تغییر، شروع و مانایی در بهبودی یکی از مهمترین اتفاقاتی است که لازم است بیفتد و دلایلی که باعث ماندگاری فرد در درمان می&zwnj;شود معمولا با گذر زمان و بهبودی تغییر می&zwnj;کند؛ پس خانواده لازم است با شرکت در جلسات تخصصی آموزش خانواده استراتژی جدید برای افزایش انگیزه را بیاموزد.</p>\r\n<p>این درمانگر اعتیاد با بیان اینکه شناخت چالش&zwnj;های عمده در مسیر بهبودی می&zwnj;تواند کمک شایانی به خانواده و فرد کند، گفت: یادتان باشد خانواده&zwnj;ای که قصد دارد در روند بهبودی فرزند معتاد خود همراه و یاور او باشد، قوانین حمایتی متناسب با سن او، بیان آزادانه احساسات، شوخی، سر زندگی، نشاط &nbsp;و خودارزشمندی را فراموش نمی&zwnj;کند و به جای غصه خوردن میدان را ترک نمی&zwnj;کند، بلکه از درمانگر کمک می&zwnj;گیرد و به فردی معتاد کمک می&zwnj;کند.</p>', '../uploadFiles/img/11550687_162.jpg  ', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(13, 3, 'فرار از صحنه تصادف رانندگی چه مجازاتی دارد؟', '<p>به گزارش خبرنگار حوزه حقوقی- قضایی گروه اجتماعی باشگاه خبرنگاران جوان، بر اساس قانون تصادف از جمله جرائمی است که شبه عمد به حساب می&zwnj;آید و از آنجایی که راننده&zwnj; متخلف در بروز حادثه سوء نیتی نداشته مطابق با قوانین شبه عمد باید مجازات شود.</p>\r\n<p>در صورتی که بر اثر بی مبالاتی و رعایت نکردن نظامات دولتی و نداشتن مهارت رانندگی تصادفی رخ دهد که منجر به قتل غیر عمد فردی دیگر شود، راننده متخلف به ۶ ماه تا ۳ سال حبس و پرداخت دیه به اولیای دم محکوم می&zwnj;شود.</p>\r\n<p>البته اگر رانندگی در حالت مستی، بدون گواهینامه، با سرعت غیر مجاز انجام شود و فرد با سرعت غیرمجاز یا با خودرویی که دارای نقص فنی موثر است و در محل ویژه عبور عابران پیاده حرکت کند، دادگاه می&zwnj;تواند متهم را تا ۵ سال از رانندگی محروم کند.</p>\r\n<p>فردی که با دیگری تصادف کرده موظف است که به هنگام تصادف رانندگی به مصدوم و سانحه دیده کمک کند و او را بلافاصله به مراکز درمانی منتقل کند، اما اگر از این وظایف خودداری و محل حادثه را ترک کند، این فرد به دو سوم حداکثر مجازات قانونی محکوم خواهد شد.</p>\r\n<p>علاوه بر آن اگر متهم به موقع به مصدوم کمک نکند، دادگاه نمی&zwnj;تواند تخفیفی در مجازات او قائل شود و اگر فردی مقصر اصلی حادثه را فراری دهد، مطابق با قانون مجازات اسلامی با او برخورد می&zwnj;شود.</p>\r\n<p>انتهای پیام/</p>', '../uploadFiles/img/11466357_824.jpg', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(14, 3, 'حذف شماره شناسنامه یکی از پیشرفت‌های ثبت احوال است', '<p>سیف الله ابوترابی سخنگوی سازمان ثبت احوال کشور در گفت&zwnj;وگو با خبرنگار حوزه رفاه و تعاون گروه اجتماعی باشگاه خبرنگاران جوان، در خصوص حذف شماره شناسنامه اظهار کرد: حذف شماره شناسنامه یا همان شماره تولد و جایگزینی شماره ملی یکی از پیشرفت&zwnj;های چشمگیر ثبت احوال از سال ۱۳۶۸ بوده است.</p>\r\n<p>او افزود: در بسیاری از کشور&zwnj;ها این فرآیند هنوز تحقق نیافته است، اما به دلیل اهمیت آن در حقوق شهروندی، از الزامات اساسی اهداف توسعه پایدار قلمداد می&zwnj;شود.</p>', '../uploadFiles/img/11298650_511.jpg', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(15, 6, '۶ اولویت بانک مرکزی برای اجرایی شدن شعار جهش تولید', '<p>به گزارش &nbsp;گروه اقتصادی باشگاه خبرنگاران جوان، عبدالناصر همتی، رئیس کل بانک مرکزی در اولین پست اینستاگرامی خود در سال جدید و با اشاره به شعار سال \"جهش تولید\" این طور نوشت: بانک مرکزی ایران به عنوان متولی سیاست های پولی و ارزی، برای پشتیبانی از \"جهش تولید\" باید به شکل دادن محیطی کمک کند که سیاست های کلان اقتصادی، شفافیت و میش بینی پذیر باشند و ریسکها برای سرمایه گذاری و تولید قابل مدیریت باشد.</p>', '../uploadFiles/img/11585224_630.jpg', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(16, 6, 'انجام پرواز هواپیما‌ها بر اساس ساعت رسمی کشور', '<p>به گزارش &nbsp;گروه اقتصادی باشگاه خبرنگاران جوان، سازمان هواپیمایی کشور &nbsp;همزمان با تغییر ساعت رسمی کشور از امروز (اول فروردین&zwnj;) اعلام کرد: با توجه به اینکه ساعت رسمی کشور از ساعت ۲۴ امشب اول فروردین ٩٩، یک ساعت به جلو کشیده می&zwnj;شود؛ لازم است مسافران پرواز&zwnj;های داخلی و خارجی ضمن تنظیم ساعت خود با ساعت رسمی کشور، برنامه&zwnj;های خود را براساس زمان درج شده در بلیت&zwnj;های هواپیما&zwnj;ها انجام دهند و در فرودگاه حضور یابند تا از پرواز&zwnj;ها جا نمانند.<br />همه کادر پرواز&zwnj;های داخلی و خارجی نیز زمان پرواز خود را بر اساس زمان رسمی ایران تنظیم کرده&zwnj;اند، از این رو مسافران باید طبق زمان درج شده در بلیت در فرودگاه حضور یابند.</p>', '../uploadFiles/img/11583974_219.jpg', 1, 0, 0, 0, 1, '2020-03-21 00:00:00'),
(17, 6, 'تولید روزانه ۵۰۰ هزار متر مربع شیشه در کشور', '<p>محمدعلی قنبری رئیس اتحادیه شیشه و آئینه در گفت وگو با خبرنگار صنعت، تجارت و کشاورزی گروه اقتصادی باشگاه خبرنگاران جوان، &nbsp;درباره وضعیت بازار شیشه و آئینه در کشور اظهار کرد: روزانه ۵۰۰ هزار مترمربع شیشه در کشور تولید می&zwnj;شود، این در حالی است که ۴۰ درصد آن وارداتی است.</p>\r\n<p>قنبری با اشاره به اینکه مشکلاتی در شیشه جام ساختمانی و مشتفات آن در کشور نداریم، اظهار کرد: افزایش قیمتی برای این صنف در بازار نداریم.&nbsp;</p>\r\n<p>&nbsp;<br />رئیس اتحادیه شیشه و آئینه با تاکید بر اینکه تولیدی در کمبودی این صنف نداریم، گفت: خوشبختانه کار در این صنف در حال انجام است.</p>\r\n<p>به گفته قنبری، مشکلی در زمینه شیشه جام آیینه، شیشه هدی دوجداره و ضدگلوله نداریم.</p>\r\n<p>این مفام مسوول با اشاره به اینکه قیمت در این صنف زمانی تغییر می&zwnj;کند که ارز جهانی تغییر کند، افزود: این در حالی است که بعضی از اقلام که نیاز یخ واردات داشته باشد قیمت آن بالا می&zwnj;رود.</p>\r\n<p>قتبری ادامه داد: خوشبختانه توانسته ایم به خودکفایی برسیم و امیدواریم که این امر بتواند به صورت صددرصدی تولید داخل داشته باشد.</p>\r\n<p>قنبری با بیان اینکه این صنعت در ایران از نمونه صنایع بسیار پویا و توانمند به شمار می&zwnj;رود، تاکید کرد: امیدواریم با توسعه و رونق بتواند به پیشرفت&zwnj;های خوبی دست یابد.</p>', '../uploadFiles/img/11523918_971.jpg', 1, 0, 0, 0, 1, '2020-03-21 00:00:00');
